mapper.select:
SELECT * FROM basket
WHERE
customer_id = #{customerId} and product_id = #{productId};

mapper.update:
UPDATE basket
SET
qty = qty + #{qty}
WHERE
customer_id = #{customerId} and product_id = #{productId};

mapper.delete:
DELETE FROM basket
WHERE
customer_id = #{customerId} and product_id = #{productId};

mapper.update:
UPDATE basket set
qty = #{qty}
WHERE
customer_id = #{customerId} and product_id = #{productId};
